import java.io.*;import java.util.*;// Coordinates formatclass CoordRead{	private long x;	private long y;	public CoordRead()	{		this(0,0);	}	public CoordRead(long x_coord,long y_coord)	{		setx(x_coord);		sety(y_coord);	}	public void setx(long x_coord)	{		x=x_coord;	}	public int getx()	{		return (int)x;	}	public void sety(long y_coord)	{		y=y_coord;	}	public int gety()	{		return (int)y;	}	}// Reading Text file public class Readfile{	private Scanner input;	public  int n=0;	public  int coords[][]=new int[100][2];	public void openFile()	{		try		{			input = new Scanner(new File("coordinates.txt"));		}		catch(FileNotFoundException fileNotFoundException)		{			System.err.println("Error Opening File.");			System.exit(1);		}	}	public  void readRecords()	{		CoordRead record=new CoordRead();		int zip_id;		try		{			while(input.hasNext())			{				n++;				zip_id=input.nextInt();				record.setx(input.nextLong());				record.sety(input.nextLong());				coords[n-1][0]=record.getx();				coords[n-1][1]=record.gety();			}		}		catch(NoSuchElementException elementException)		{			System.err.println("File Improperly Formed.");			input.close();			System.exit(1);		}		catch(IllegalStateException stateException)		{			System.err.println("Error reading fromfile.");			System.exit(1);		}	}	public void closeFile()	{		if(input!=null)			input.close();	}}